EXPRESSION:
CALCULATE(
    SUMX(
        'Hiring Investment', 
        DIVIDE([Calendar Year Cost], 'Hiring Investment'[Total No. Months in Plan])
    )
)

IF(
    [ShowValueForDates],
    CALCULATE(
        [Actual AC],
        CALCULATETABLE(
            DATEADD( 'Date'[Date], -1, MONTH ),
            'Date'[DateWithValue] = TRUE
        )
    )
)

IF(
    [ShowValueForDates],
    CALCULATE(
        [Actual AC],
        CALCULATETABLE(
            DATEADD( 'Date'[Date], -1, QUARTER ),
            'Date'[DateWithValue] = TRUE
        )
    )
)

IF(
    [ShowValueForDates],
    CALCULATE(
        [Budget BU],
        CALCULATETABLE(
            DATEADD( 'Date'[Date], -1, MONTH ),
            'Date'[DateWithValue] = TRUE
        )
    )
)

IF(
    [ShowValueForDates],
    CALCULATE(
        [Budget BU],
        CALCULATETABLE(
            DATEADD( 'Date'[Date], -1, QUARTER ),
            'Date'[DateWithValue] = TRUE
        )
    )
)

IF(
    [ShowValueForDates],
    CALCULATE(
        [Headcount Hires],
        CALCULATETABLE(
            DATEADD( 'Date'[Date], -1, QUARTER ),
            'Date'[DateWithValue] = TRUE
        )
    )
)

CALCULATE(
    DISTINCTCOUNT('Hiring Investment'[Index]),
    FILTER(
        'Hiring Investment',
        'Hiring Investment'[Investment Category] IN {"FTE", "Non-FTE"}
    )
)

